macroActions.command:
replaceLayer mod CMX mod
replaceLayer mouse CMX mouse
replaceLayer fn CMX fn
replaceLayer fn2 CMX fn2
replaceLayer fn3 CMX fn3
replaceLayer fn4 CMX fn4
replaceLayer fn5 CMX fn5
set keymapAction.fn.isoKey macro initCM3
if ( $unpause == 3 ) { 
    set keymapAction.fn.b macro initCM3 
}
else if ( $unpause == 2 ) {
    set keymapAction.fn.b macro initCM2
}
else { 
    set keymapAction.fn.b macro initCMX
}
call keepAlive

ifKeyActive $keyId.q {
    ifShortcut timeoutIn 100 $keyId.w final holdKey escape
    else final holdKey q
}
ifKeyActive $keyId.w {
    ifShortcut timeoutIn 100 $keyId.q final holdKey escape
    else final holdKey w
}

if ( $snap_running == 0 ) {
    setVar snap_mode 0
    fork snap-leftright-worker
}
if ($thisKeyId == $keyId.j) {
    setVar snap_mode 1
}
else if ($thisKeyId == $keyId.l) {
    setVar snap_mode 2
}
delayUntilRelease
ifNotKeyActive $keyId.j ifNotKeyActive $keyId.l final setVar snap_mode 0
ifKeyActive $keyId.j final setVar snap_mode 1
ifKeyActive $keyId.l final setVar snap_mode 2

setVar snap_running 1
setVar snap_state 0
// snap_state: current state of left/right movement
// snap_mode: target state of left/right movement
// The worker will send key press/release actions to move
// the current state to the target state.
loop:
if ($snap_state == 0) {
    if ($snap_mode == 1) {
        releaseKey right
        pressKey left
    }
    else if ($snap_mode == 2) {
        releaseKey left
        pressKey right
    }
}
else if ($snap_state == 1) {
    if ($snap_mode == 0) {
        releaseKey left
        releaseKey right
    }
    else if ($snap_mode == 2) {
        releaseKey left
        pressKey right
    }
}
else if ($snap_state == 2) {
    if ($snap_mode == 0) {
        releaseKey right
        releaseKey left
    }
    else if ($snap_mode == 1) {
        releaseKey right
        pressKey left
    }
}
setVar snap_state $snap_mode
goTo loop
// ifNotPlaytime 10000 goTo loop // use this for debugging
// setVar snap_running 0

ifSecondary final suppressMods holdKey oRC-
ifShift {
    suppressMods write ThisWouldBeThePwd

}
else {
    delayUntil 200
    suppressMods tapKeySeq m y u s e r i d
}
